import { useState, useEffect } from 'react';
import {
  BookOpen,
  Plus,
  Users,
  Calendar,
  Clock,
  Pencil,
  Trash2,
  GraduationCap,
  TrendingUp,
  Tag
} from 'lucide-react';
import { Button } from '@/components/ui/button';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { Badge } from '@/components/ui/badge';
import { Input } from '@/components/ui/input';
import {
  Dialog,
  DialogContent,
  DialogHeader,
  DialogTitle,
  DialogFooter
} from '@/components/ui/dialog';
import {
  Select,
  SelectContent,
  SelectItem,
  SelectTrigger,
  SelectValue,
} from '@/components/ui/select';
import {
  AlertDialog,
  AlertDialogAction,
  AlertDialogCancel,
  AlertDialogContent,
  AlertDialogDescription,
  AlertDialogFooter,
  AlertDialogHeader,
  AlertDialogTitle,
} from '@/components/ui/alert-dialog';

// Types
interface Category {
  record_id: string;
  name: string;
}

interface Course {
  record_id: string;
  title: string;
  description: string;
  category_id: string;
  instructor: string;
  max_participants: number;
  current_participants: number;
  start_date: string;
  end_date: string;
  status: 'active' | 'upcoming' | 'completed';
}

// Mock Data
const mockCategories: Category[] = [
  { record_id: '1', name: 'Programmierung' },
  { record_id: '2', name: 'Design' },
  { record_id: '3', name: 'Marketing' },
  { record_id: '4', name: 'Sprachen' },
];

const mockCourses: Course[] = [
  {
    record_id: '1',
    title: 'React Fundamentals',
    description: 'Grundlagen der React-Entwicklung mit Hooks und modernem State Management',
    category_id: '1',
    instructor: 'Max Mustermann',
    max_participants: 20,
    current_participants: 15,
    start_date: '2024-03-01',
    end_date: '2024-04-15',
    status: 'active',
  },
  {
    record_id: '2',
    title: 'UI/UX Design Basics',
    description: 'Einf√ºhrung in User Interface und User Experience Design',
    category_id: '2',
    instructor: 'Anna Schmidt',
    max_participants: 15,
    current_participants: 12,
    start_date: '2024-03-15',
    end_date: '2024-05-01',
    status: 'active',
  },
  {
    record_id: '3',
    title: 'Python f√ºr Anf√§nger',
    description: 'Programmieren lernen mit Python von Grund auf',
    category_id: '1',
    instructor: 'Thomas Weber',
    max_participants: 25,
    current_participants: 0,
    start_date: '2024-04-01',
    end_date: '2024-06-01',
    status: 'upcoming',
  },
  {
    record_id: '4',
    title: 'Content Marketing',
    description: 'Strategien f√ºr erfolgreiches Content Marketing',
    category_id: '3',
    instructor: 'Lisa M√ºller',
    max_participants: 18,
    current_participants: 18,
    start_date: '2024-01-15',
    end_date: '2024-02-28',
    status: 'completed',
  },
  {
    record_id: '5',
    title: 'Business Englisch',
    description: 'Professionelles Englisch f√ºr den Gesch√§ftsalltag',
    category_id: '4',
    instructor: 'John Smith',
    max_participants: 12,
    current_participants: 8,
    start_date: '2024-03-10',
    end_date: '2024-05-20',
    status: 'active',
  },
];

export default function Dashboard() {
  // State
  const [courses, setCourses] = useState<Course[]>(mockCourses);
  const [categories, setCategories] = useState<Category[]>(mockCategories);
  const [loading, setLoading] = useState(false);

  // Course Dialog State
  const [courseDialogOpen, setCourseDialogOpen] = useState(false);
  const [editingCourse, setEditingCourse] = useState<Course | null>(null);
  const [courseForm, setCourseForm] = useState({
    title: '',
    description: '',
    category_id: '',
    instructor: '',
    max_participants: 20,
    start_date: '',
    end_date: '',
    status: 'upcoming' as Course['status'],
  });

  // Category Dialog State
  const [categoryDialogOpen, setCategoryDialogOpen] = useState(false);
  const [editingCategory, setEditingCategory] = useState<Category | null>(null);
  const [categoryForm, setCategoryForm] = useState({ name: '' });

  // Delete Dialog State
  const [deleteDialog, setDeleteDialog] = useState<{
    open: boolean;
    type: 'course' | 'category';
    id: string;
    name: string;
  }>({ open: false, type: 'course', id: '', name: '' });

  // Filter State
  const [filterCategory, setFilterCategory] = useState<string>('all');
  const [filterStatus, setFilterStatus] = useState<string>('all');

  // Computed values
  const filteredCourses = courses.filter(course => {
    if (filterCategory !== 'all' && course.category_id !== filterCategory) return false;
    if (filterStatus !== 'all' && course.status !== filterStatus) return false;
    return true;
  });

  const stats = {
    totalCourses: courses.length,
    activeCourses: courses.filter(c => c.status === 'active').length,
    totalParticipants: courses.reduce((sum, c) => sum + c.current_participants, 0),
    categories: categories.length,
  };

  // Course CRUD
  const openCourseDialog = (course?: Course) => {
    if (course) {
      setEditingCourse(course);
      setCourseForm({
        title: course.title,
        description: course.description,
        category_id: course.category_id,
        instructor: course.instructor,
        max_participants: course.max_participants,
        start_date: course.start_date,
        end_date: course.end_date,
        status: course.status,
      });
    } else {
      setEditingCourse(null);
      setCourseForm({
        title: '',
        description: '',
        category_id: categories[0]?.record_id || '',
        instructor: '',
        max_participants: 20,
        start_date: '',
        end_date: '',
        status: 'upcoming',
      });
    }
    setCourseDialogOpen(true);
  };

  const saveCourse = () => {
    if (!courseForm.title || !courseForm.instructor || !courseForm.category_id) return;

    if (editingCourse) {
      setCourses(prev => prev.map(c =>
        c.record_id === editingCourse.record_id
          ? { ...c, ...courseForm, current_participants: c.current_participants }
          : c
      ));
    } else {
      const newCourse: Course = {
        record_id: Date.now().toString(),
        ...courseForm,
        current_participants: 0,
      };
      setCourses(prev => [...prev, newCourse]);
    }
    setCourseDialogOpen(false);
  };

  const deleteCourse = (id: string) => {
    setCourses(prev => prev.filter(c => c.record_id !== id));
    setDeleteDialog({ open: false, type: 'course', id: '', name: '' });
  };

  // Category CRUD
  const openCategoryDialog = (category?: Category) => {
    if (category) {
      setEditingCategory(category);
      setCategoryForm({ name: category.name });
    } else {
      setEditingCategory(null);
      setCategoryForm({ name: '' });
    }
    setCategoryDialogOpen(true);
  };

  const saveCategory = () => {
    if (!categoryForm.name) return;

    if (editingCategory) {
      setCategories(prev => prev.map(c =>
        c.record_id === editingCategory.record_id
          ? { ...c, name: categoryForm.name }
          : c
      ));
    } else {
      const newCategory: Category = {
        record_id: Date.now().toString(),
        name: categoryForm.name,
      };
      setCategories(prev => [...prev, newCategory]);
    }
    setCategoryDialogOpen(false);
  };

  const deleteCategory = (id: string) => {
    setCategories(prev => prev.filter(c => c.record_id !== id));
    setDeleteDialog({ open: false, type: 'category', id: '', name: '' });
  };

  // Helper functions
  const getCategoryName = (id: string) => {
    return categories.find(c => c.record_id === id)?.name || 'Unbekannt';
  };

  const getStatusColor = (status: Course['status']) => {
    switch (status) {
      case 'active': return 'bg-status-active';
      case 'upcoming': return 'bg-status-upcoming';
      case 'completed': return 'bg-status-completed';
    }
  };

  const getStatusLabel = (status: Course['status']) => {
    switch (status) {
      case 'active': return 'Aktiv';
      case 'upcoming': return 'Geplant';
      case 'completed': return 'Abgeschlossen';
    }
  };

  const formatDate = (dateString: string) => {
    return new Date(dateString).toLocaleDateString('de-DE', {
      day: '2-digit',
      month: '2-digit',
      year: 'numeric',
    });
  };

  return (
    <div className="min-h-screen bg-background">
      {/* Header */}
      <header className="hero-gradient text-hero px-4 py-8 sm:px-6 lg:px-8">
        <div className="mx-auto max-w-7xl">
          <div className="flex flex-col gap-6 sm:flex-row sm:items-center sm:justify-between">
            <div>
              <h1 className="text-3xl font-bold tracking-tight sm:text-4xl">
                Kursverwaltung
              </h1>
              <p className="mt-2 text-hero-muted">
                Verwalten Sie Ihre Kurse und Kategorien
              </p>
            </div>
            <Button
              onClick={() => openCourseDialog()}
              className="hero-shadow bg-card text-foreground hover:bg-card/90 transition-smooth self-start sm:self-center"
              size="lg"
            >
              <Plus className="mr-2 size-5" />
              Neuer Kurs
            </Button>
          </div>

          {/* Hero Stats */}
          <div className="mt-8 grid grid-cols-2 gap-4 sm:grid-cols-4">
            <div className="rounded-xl bg-card/10 backdrop-blur-sm p-4 transition-smooth hover:bg-card/20">
              <div className="flex items-center gap-3">
                <BookOpen className="size-8 text-hero-muted" />
                <div>
                  <p className="text-3xl font-bold">{stats.totalCourses}</p>
                  <p className="text-sm text-hero-muted">Kurse gesamt</p>
                </div>
              </div>
            </div>
            <div className="rounded-xl bg-card/10 backdrop-blur-sm p-4 transition-smooth hover:bg-card/20">
              <div className="flex items-center gap-3">
                <TrendingUp className="size-8 text-hero-muted" />
                <div>
                  <p className="text-3xl font-bold">{stats.activeCourses}</p>
                  <p className="text-sm text-hero-muted">Aktive Kurse</p>
                </div>
              </div>
            </div>
            <div className="rounded-xl bg-card/10 backdrop-blur-sm p-4 transition-smooth hover:bg-card/20">
              <div className="flex items-center gap-3">
                <Users className="size-8 text-hero-muted" />
                <div>
                  <p className="text-3xl font-bold">{stats.totalParticipants}</p>
                  <p className="text-sm text-hero-muted">Teilnehmer</p>
                </div>
              </div>
            </div>
            <div className="rounded-xl bg-card/10 backdrop-blur-sm p-4 transition-smooth hover:bg-card/20">
              <div className="flex items-center gap-3">
                <Tag className="size-8 text-hero-muted" />
                <div>
                  <p className="text-3xl font-bold">{stats.categories}</p>
                  <p className="text-sm text-hero-muted">Kategorien</p>
                </div>
              </div>
            </div>
          </div>
        </div>
      </header>

      {/* Main Content */}
      <main className="mx-auto max-w-7xl px-4 py-8 sm:px-6 lg:px-8">
        {/* Categories Section */}
        <section className="mb-10">
          <div className="flex items-center justify-between mb-4">
            <h2 className="text-xl font-semibold flex items-center gap-2">
              <Tag className="size-5 text-primary" />
              Kategorien
            </h2>
            <Button
              variant="outline"
              size="sm"
              onClick={() => openCategoryDialog()}
            >
              <Plus className="mr-1 size-4" />
              Kategorie
            </Button>
          </div>
          <div className="flex flex-wrap gap-2">
            {categories.map(category => (
              <Badge
                key={category.record_id}
                variant="secondary"
                className="px-3 py-1.5 text-sm cursor-pointer group transition-smooth hover:bg-secondary/80"
              >
                {category.name}
                <span className="ml-2 opacity-0 group-hover:opacity-100 transition-smooth inline-flex gap-1">
                  <button
                    onClick={(e) => { e.stopPropagation(); openCategoryDialog(category); }}
                    className="hover:text-primary"
                  >
                    <Pencil className="size-3" />
                  </button>
                  <button
                    onClick={(e) => {
                      e.stopPropagation();
                      setDeleteDialog({ open: true, type: 'category', id: category.record_id, name: category.name });
                    }}
                    className="hover:text-destructive"
                  >
                    <Trash2 className="size-3" />
                  </button>
                </span>
              </Badge>
            ))}
          </div>
        </section>

        {/* Filters */}
        <section className="mb-6 flex flex-wrap gap-4">
          <Select value={filterCategory} onValueChange={setFilterCategory}>
            <SelectTrigger className="w-[180px]">
              <SelectValue placeholder="Kategorie filtern" />
            </SelectTrigger>
            <SelectContent>
              <SelectItem value="all">Alle Kategorien</SelectItem>
              {categories.map(cat => (
                <SelectItem key={cat.record_id} value={cat.record_id}>
                  {cat.name}
                </SelectItem>
              ))}
            </SelectContent>
          </Select>
          <Select value={filterStatus} onValueChange={setFilterStatus}>
            <SelectTrigger className="w-[180px]">
              <SelectValue placeholder="Status filtern" />
            </SelectTrigger>
            <SelectContent>
              <SelectItem value="all">Alle Status</SelectItem>
              <SelectItem value="active">Aktiv</SelectItem>
              <SelectItem value="upcoming">Geplant</SelectItem>
              <SelectItem value="completed">Abgeschlossen</SelectItem>
            </SelectContent>
          </Select>
        </section>

        {/* Courses Grid */}
        <section>
          <h2 className="text-xl font-semibold mb-4 flex items-center gap-2">
            <GraduationCap className="size-5 text-primary" />
            Kurse ({filteredCourses.length})
          </h2>

          {filteredCourses.length === 0 ? (
            <Card className="card-shadow">
              <CardContent className="py-12 text-center">
                <BookOpen className="mx-auto size-12 text-muted-foreground mb-4" />
                <p className="text-muted-foreground">Keine Kurse gefunden</p>
                <Button
                  variant="outline"
                  className="mt-4"
                  onClick={() => openCourseDialog()}
                >
                  <Plus className="mr-2 size-4" />
                  Ersten Kurs erstellen
                </Button>
              </CardContent>
            </Card>
          ) : (
            <div className="grid gap-4 sm:grid-cols-2 lg:grid-cols-3">
              {filteredCourses.map(course => (
                <Card
                  key={course.record_id}
                  className="card-shadow transition-smooth hover:shadow-lg group"
                >
                  <CardHeader className="pb-3">
                    <div className="flex items-start justify-between">
                      <Badge
                        className={`${getStatusColor(course.status)} text-card border-0`}
                      >
                        {getStatusLabel(course.status)}
                      </Badge>
                      <div className="flex gap-1 opacity-0 group-hover:opacity-100 transition-smooth">
                        <Button
                          variant="ghost"
                          size="icon-sm"
                          onClick={() => openCourseDialog(course)}
                        >
                          <Pencil className="size-4" />
                        </Button>
                        <Button
                          variant="ghost"
                          size="icon-sm"
                          onClick={() => setDeleteDialog({
                            open: true,
                            type: 'course',
                            id: course.record_id,
                            name: course.title
                          })}
                        >
                          <Trash2 className="size-4 text-destructive" />
                        </Button>
                      </div>
                    </div>
                    <CardTitle className="mt-2 text-lg leading-tight">
                      {course.title}
                    </CardTitle>
                    <p className="text-sm text-muted-foreground line-clamp-2">
                      {course.description}
                    </p>
                  </CardHeader>
                  <CardContent className="space-y-3">
                    <div className="flex items-center gap-2 text-sm">
                      <Tag className="size-4 text-muted-foreground" />
                      <span className="text-muted-foreground">
                        {getCategoryName(course.category_id)}
                      </span>
                    </div>
                    <div className="flex items-center gap-2 text-sm">
                      <Users className="size-4 text-muted-foreground" />
                      <span>
                        {course.current_participants} / {course.max_participants} Teilnehmer
                      </span>
                      {course.current_participants >= course.max_participants && (
                        <Badge variant="destructive" className="text-xs">Voll</Badge>
                      )}
                    </div>
                    <div className="flex items-center gap-2 text-sm text-muted-foreground">
                      <Calendar className="size-4" />
                      <span>
                        {formatDate(course.start_date)} - {formatDate(course.end_date)}
                      </span>
                    </div>
                    <div className="flex items-center gap-2 text-sm text-muted-foreground">
                      <GraduationCap className="size-4" />
                      <span>{course.instructor}</span>
                    </div>

                    {/* Participant Progress */}
                    <div className="pt-2">
                      <div className="h-2 bg-muted rounded-full overflow-hidden">
                        <div
                          className="h-full bg-primary transition-all duration-500"
                          style={{
                            width: `${(course.current_participants / course.max_participants) * 100}%`
                          }}
                        />
                      </div>
                    </div>
                  </CardContent>
                </Card>
              ))}
            </div>
          )}
        </section>
      </main>

      {/* Course Dialog */}
      <Dialog open={courseDialogOpen} onOpenChange={setCourseDialogOpen}>
        <DialogContent className="sm:max-w-lg">
          <DialogHeader>
            <DialogTitle>
              {editingCourse ? 'Kurs bearbeiten' : 'Neuer Kurs'}
            </DialogTitle>
          </DialogHeader>
          <div className="space-y-4 py-4">
            <div className="space-y-2">
              <label className="text-sm font-medium">Titel *</label>
              <Input
                value={courseForm.title}
                onChange={e => setCourseForm(prev => ({ ...prev, title: e.target.value }))}
                placeholder="Kurstitel eingeben"
              />
            </div>
            <div className="space-y-2">
              <label className="text-sm font-medium">Beschreibung</label>
              <Input
                value={courseForm.description}
                onChange={e => setCourseForm(prev => ({ ...prev, description: e.target.value }))}
                placeholder="Kurze Beschreibung"
              />
            </div>
            <div className="grid grid-cols-2 gap-4">
              <div className="space-y-2">
                <label className="text-sm font-medium">Kategorie *</label>
                <Select
                  value={courseForm.category_id}
                  onValueChange={value => setCourseForm(prev => ({ ...prev, category_id: value }))}
                >
                  <SelectTrigger className="w-full">
                    <SelectValue placeholder="W√§hlen..." />
                  </SelectTrigger>
                  <SelectContent>
                    {categories.map(cat => (
                      <SelectItem key={cat.record_id} value={cat.record_id}>
                        {cat.name}
                      </SelectItem>
                    ))}
                  </SelectContent>
                </Select>
              </div>
              <div className="space-y-2">
                <label className="text-sm font-medium">Status</label>
                <Select
                  value={courseForm.status}
                  onValueChange={value => setCourseForm(prev => ({
                    ...prev,
                    status: value as Course['status']
                  }))}
                >
                  <SelectTrigger className="w-full">
                    <SelectValue />
                  </SelectTrigger>
                  <SelectContent>
                    <SelectItem value="upcoming">Geplant</SelectItem>
                    <SelectItem value="active">Aktiv</SelectItem>
                    <SelectItem value="completed">Abgeschlossen</SelectItem>
                  </SelectContent>
                </Select>
              </div>
            </div>
            <div className="space-y-2">
              <label className="text-sm font-medium">Kursleiter *</label>
              <Input
                value={courseForm.instructor}
                onChange={e => setCourseForm(prev => ({ ...prev, instructor: e.target.value }))}
                placeholder="Name des Kursleiters"
              />
            </div>
            <div className="space-y-2">
              <label className="text-sm font-medium">Max. Teilnehmer</label>
              <Input
                type="number"
                value={courseForm.max_participants}
                onChange={e => setCourseForm(prev => ({
                  ...prev,
                  max_participants: parseInt(e.target.value) || 0
                }))}
                min={1}
              />
            </div>
            <div className="grid grid-cols-2 gap-4">
              <div className="space-y-2">
                <label className="text-sm font-medium">Startdatum</label>
                <Input
                  type="date"
                  value={courseForm.start_date}
                  onChange={e => setCourseForm(prev => ({ ...prev, start_date: e.target.value }))}
                />
              </div>
              <div className="space-y-2">
                <label className="text-sm font-medium">Enddatum</label>
                <Input
                  type="date"
                  value={courseForm.end_date}
                  onChange={e => setCourseForm(prev => ({ ...prev, end_date: e.target.value }))}
                />
              </div>
            </div>
          </div>
          <DialogFooter>
            <Button variant="outline" onClick={() => setCourseDialogOpen(false)}>
              Abbrechen
            </Button>
            <Button onClick={saveCourse}>
              {editingCourse ? 'Speichern' : 'Erstellen'}
            </Button>
          </DialogFooter>
        </DialogContent>
      </Dialog>

      {/* Category Dialog */}
      <Dialog open={categoryDialogOpen} onOpenChange={setCategoryDialogOpen}>
        <DialogContent className="sm:max-w-md">
          <DialogHeader>
            <DialogTitle>
              {editingCategory ? 'Kategorie bearbeiten' : 'Neue Kategorie'}
            </DialogTitle>
          </DialogHeader>
          <div className="space-y-4 py-4">
            <div className="space-y-2">
              <label className="text-sm font-medium">Name *</label>
              <Input
                value={categoryForm.name}
                onChange={e => setCategoryForm({ name: e.target.value })}
                placeholder="Kategoriename eingeben"
              />
            </div>
          </div>
          <DialogFooter>
            <Button variant="outline" onClick={() => setCategoryDialogOpen(false)}>
              Abbrechen
            </Button>
            <Button onClick={saveCategory}>
              {editingCategory ? 'Speichern' : 'Erstellen'}
            </Button>
          </DialogFooter>
        </DialogContent>
      </Dialog>

      {/* Delete Confirmation */}
      <AlertDialog
        open={deleteDialog.open}
        onOpenChange={(open) => setDeleteDialog(prev => ({ ...prev, open }))}
      >
        <AlertDialogContent>
          <AlertDialogHeader>
            <AlertDialogTitle>
              {deleteDialog.type === 'course' ? 'Kurs l√∂schen?' : 'Kategorie l√∂schen?'}
            </AlertDialogTitle>
            <AlertDialogDescription>
              M√∂chten Sie "{deleteDialog.name}" wirklich l√∂schen? Diese Aktion kann nicht r√ºckg√§ngig gemacht werden.
            </AlertDialogDescription>
          </AlertDialogHeader>
          <AlertDialogFooter>
            <AlertDialogCancel>Abbrechen</AlertDialogCancel>
            <AlertDialogAction
              onClick={() => {
                if (deleteDialog.type === 'course') {
                  deleteCourse(deleteDialog.id);
                } else {
                  deleteCategory(deleteDialog.id);
                }
              }}
              className="bg-destructive hover:bg-destructive/90"
            >
              L√∂schen
            </AlertDialogAction>
          </AlertDialogFooter>
        </AlertDialogContent>
      </AlertDialog>
    </div>
  );
}
